package de.hdm_stuttgart.huber.itprojekt.shared.domainobjects;

import java.sql.Date;

/**
 * @author elcpt
 *
 */
public class NoteBook extends DomainObject {

	/**
	 * #AUTOGENERATED
	 */
	private static final long serialVersionUID = 1L;

	private String title = null;
	private String subtitle = null;
	private UserInfo owner = null;
	private Date creationDate = null;
	private Date modificationDate = null;

    public NoteBook() {
    }

    public NoteBook(int id, String title, String subtitle, UserInfo owner, Date creationDate, Date modificationDate) {

        this.id = id;
        this.title = title;
        this.subtitle = subtitle;
        this.owner = owner;
        this.creationDate = creationDate;
        this.modificationDate = modificationDate;
    }

    // REINE TESTMETHODE!!!
    public NoteBook(int id) {
        this.id = id;
    }
    
    @Override
	public String toString() {
		return "NoteBook{" +
				"Titel='" + title + '\'' +
				", Untertitel='" + subtitle + '\'' +
				", Autor=" + owner +
				", Erstellt am:" + creationDate +
				", Zuletzt modifiziert am:" + modificationDate +
				'}';
	}
    
    

	public int getId() {
        return this.id;
    }
    public String getTitle() {
        return title;
    }

    public String getSubtitle() {
        return subtitle;
    }

    public UserInfo getOwner() {
        return owner;
    }

    public Date getCreationDate() {
        return creationDate;
    }

    public Date getModificationDate() {
        return modificationDate;
    }

    public void setTitle(String title) {
        this.title = title;
    }

    public void setSubtitle(String subtitle) {
        this.subtitle = subtitle;
    }

    public void setOwner(UserInfo owner) {
        this.owner = owner;
    }

    public void setCreationDate(Date creationDate) {
        this.creationDate = creationDate;
    }

    public void setModificationDate(Date modificationDate) {
        this.modificationDate = modificationDate;
    }
}
